import { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import "../App.css";

function AdminLogin() {
  const [fileData, setData] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...fileData, [input.name]: input.value });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div className="admin-wrapper align-item-center d-flex justify-content-center">
      <div className="row justify-content-center align-items-center admin-form-wrapper">
        <div className="col-lg-9 form-content-wrapper text-center">
          <form onSubmit={handleFormSubmit}>
            <div className="form-group d-flex m-4 align-items-center">
              <i className="fa fa-user"></i>
              <input
                type="text"
                className="form-control"
                placeholder="email"
                name="email"
                onChange={handleChange}
                value={fileData.email}
                required="required"
              />
            </div>
            <div className="form-group d-flex m-4 align-items-center">
              <i className="fa fa-lock"></i>
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                name="password"
                required="required"
                onChange={handleChange}
                value={fileData.password}
              />
            </div>
            <div className="form-group align-items-center m-4 d-flex justify-content-center">
              {error && <div className="text-danger">{error}</div>}
              <input
                type="submit"
                className="btn btn-primary btn-block btn-lg"
                value="Login"
              />
            </div>
            <p>
              Dont have an account?{" "}
              <Link to="/signup">
                <button className="btn btn-white">
                  Click here to register
                </button>
              </Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AdminLogin;
