import PageWrapper from "./PageWrapper";
import React from 'react'
import { useState } from "react";


function Profile() {
    const [image, setImage ] = useState("");
const [ url, setUrl ] = useState("");
const uploadImage = () => {
const data = new FormData()
data.append("file", image)
data.append("upload_preset", "tutorial")
data.append("cloud_name","breellz")
fetch("  https://api.cloudinary.com/v1_1/breellz/image/upload",{
method:"post",
body: data
})
.then(resp => resp.json())
.then(data => {
setUrl(data.url)
})
.catch(err => console.log(err))
}
    return (
        <PageWrapper>
        <div className="justify-content-center">
        <h1>Profile Page</h1>
        <img className="my-3" src="https://cdn-icons-png.flaticon.com/512/1250/1250689.png" width={70} height={70}  alt="profile"/>
        <p>Full Name : Joe Mama</p>
        <p>Email : joemama@gmail.com</p>
        <p>Mobile : 225522552255</p>
        <label className="btn btn-dark"><input onChange={handleChange} className="ms-3 joe" type="file"/>Choose a photo</label>
        <button className="btn btn-primary mx-2" onClick={onSubmit}>Upload</button>
        <h4>Or</h4>
        <button className="btn btn-danger">Logout</button>
    </div>
    </PageWrapper>
    )
}

export default Profile
