import { useState } from "react";

function SignUp() {
  const handleFormSubmit = (event) => {
    event.preventDefault();
  };
  const [inputName, setInputName] = useState("");
  const [inputEmail, setInputEmail] = useState("");
  const [inputPassword, setInputPassword] = useState("");
  const [disabled, setDisabled]  = useState(true)

  function disabledFunction(){
    if(inputName ==='' && inputEmail===''&& inputPassword===''&&inputName.length<4 && inputEmail<5||inputPassword<5){
        setDisabled(true)
      }else{
        setDisabled(false)
      }
  }
 
 
  const handleInputName = (event) => {
    disabledFunction()
    setInputName(event.target.value);
  };
  const handleInputEmail = (event) => {
    disabledFunction()
    setInputEmail(event.target.value);
    
  };
  const handleInputPassword = (event) => {
    disabledFunction()
    setInputPassword(event.target.value);
  };


  return (
    <div>
      <h1>Sign Up</h1>
      <form onSubmit={handleFormSubmit}>
        <div className="form-group d-flex m-4 align-items-center">
          <input
            onChange={handleInputName}
            value={inputName}
            type="text"
            className="form-control"
            placeholder="Full name"
          />
        </div>

        <div className="form-group d-flex m-4 align-items-center">
          <input
          onChange={handleInputEmail}
          value={inputEmail}
            type="text"
            className="form-control"
            placeholder="Email"
           
          />
        </div>

        <div className="form-group d-flex m-4 align-items-center">
          <input
          onChange={handleInputPassword}
          value={inputPassword}
            type="password"
            className="form-control"
            placeholder="Password"
           
          />
        </div>

        <div className="form-group align-items-center m-4 d-flex justify-content-center">
          <button
            type="submit"
            className="btn btn-primary btn-block btn-lg"
            disabled={disabled}
            >Sign Up</button>
        </div>
      </form>
    </div>
  );
}

export default SignUp;
