import PageWrapper from "./PageWrapper";
import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { useSelector } from 'react-redux';

function Profile() {
  const email = useSelector(state => state.email);
 
  const [url, setUrl] = useState("");

 const handleImageUpload = async (event) => {
    const file = event.target.files[0];
  
    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', 'n0d0jino');
  
    try {
      const response = await axios.post(
        'https://api.cloudinary.com/v1_1/dqrpxoouq/image/upload',
        formData
      );
      
      const imageUrl = response.data.secure_url;
      console.log(imageUrl)
      setUrl( imageUrl );
    } catch (error) {
      console.error(error);
    }
  };
  
  return (
    <PageWrapper>
      <div className="justify-content-center">
        <h1>Profile Page</h1>
        <p>{email ? JSON.stringify(email) : 'No data found.'}</p>
        <img
          className="my-3"
          src={url}
          width={70}
          height={70}
          alt="profile"
        />
        <p>Full Name : Joe Mama</p>
        <p>Email : joemama@gmail.com</p>
        <p>Mobile : 225522552255</p>
        <label className="btn btn-dark">
        <input type="file" onChange= {handleImageUpload}></input>
          Choose a photo
        </label>
        
        <h4>Or</h4>
        <button className="btn btn-danger">Logout</button>
      </div>
    </PageWrapper>
  );
}

export default Profile;
