import axios from "axios";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import "../App.css";

function AdminLogin() {
  const [fileData, setData] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...fileData, [input.name]: input.value });
  };

  const handleFormSubmit = async(event) => {
    event.preventDefault();
    try {
        const url = "http://localhost:3000/api/admin/auth";
        const { data: res } = await axios.post(url, fileData);
        localStorage.setItem("admin-token", res.data.token);
        
        
        const getUserData = async () => {
         
            
            let data = { userEmail: fileData.email };
            const url = "http://localhost:3000/api/admin/user-data";
            const { data: res } = await axios.post(url, data);
            localStorage.setItem("userData", JSON.stringify(res.data) );
            dispatch(subscribeUser(res.data));
            navigate("/");
         
        };
  
        getUserData();
      } catch (error) {
        if (
          error.response &&
          error.response.status >= 400 &&
          error.response.status <= 500
        ) {
          setError(error.response.data.message);
        }
      }
  
  };

  return (
    <div className="admin-wrapper align-item-center d-flex justify-content-center">
      <div className="row justify-content-center align-items-center admin-form-wrapper">
        <div className="col-lg-9 admin-content-wrapper text-center">
          <h1 className="text-white">Admin Sign In</h1>
          <form onSubmit={handleFormSubmit}>
            <div className="form-group d-flex m-4 align-items-center">
              <i className="fa fa-user text-white"></i>
              <input
                type="text"
                className="form-control"
                placeholder="email"
                name="email"
                onChange={handleChange}
                value={fileData.email}
                required="required"
              />
            </div>
            <div className="form-group d-flex m-4 align-items-center">
              <i className="fa fa-lock text-white"></i>
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                name="password"
                required="required"
                onChange={handleChange}
                value={fileData.password}
              />
            </div>
            <div className="form-group align-items-center m-4 d-flex justify-content-center">
              {error && <div className="text-danger">{error}</div>}
              <input
                type="submit"
                className="btn btn-dark btn-block btn-lg"
                value="Login"
              />
            </div>
            
          </form>
        </div>
      </div>
    </div>
  );
}

export default AdminLogin;
